(function (define) {
    'use strict';

    (function (root, factory) {
        if (typeof define === 'function' && define.amd) {
            define(jetformspanel,factory);
        } else if (typeof exports === 'object') {
            module.exports = factory();
        } else {
            root.jetformspanel = factory();
        }
    }(window, function () {
        return (function () {










            var jetformspanel = function()
            {

                 jetformspanel.RowContainer = "NotAssigned";

                function jetformspanel(ObjectToApend,JSONSetup)
                {
                    jetformspanel.DoLayout(ObjectToApend,JSONSetup);

                }



                jetformspanel.mainblock = function (ObjectToApend,panelName)
                {
                    var main = document.createElement("div");
                    // main.classList.add("col-sm-20");
                    // main.classList.add("col-lg-20");
                    // main.setAttribute("style", "height:50%; margin :10px");

                    var baseElement = document.getElementById(ObjectToApend);

                    //document.body.appendChild(main);
                    baseElement.appendChild(main);
                    var dashMain= document.createElement("div");
                    dashMain.classList.add("dash-MainPannel");
                    main.appendChild(dashMain);
                    var dashMainHeader= document.createElement("div");
                    dashMainHeader.setAttribute("style", "background-color:#0079F1; width 100%");
                    dashMain.appendChild(dashMainHeader);
                    var dashMainHeaderText= document.createElement("h5");
                    dashMainHeaderText.setAttribute("style", "color:#FFF");
                    dashMainHeaderText.innerHTML = panelName;
                    dashMainHeader.appendChild(dashMainHeaderText);
                    var divContainer  = document.createElement("div");
                    divContainer.classList.add("container");
                    dashMain.appendChild(divContainer);


                    return divContainer;
                }

                jetformspanel.createRow = function(main)
                {
                    var divRow  = document.createElement("div");
                    divRow.classList.add("row-flex");
                    main.appendChild(divRow);
                    jetformspanel.RowContainer = divRow;
                    return divRow;
                }
                jetformspanel.addRow = function(main)
                {
                    if (jetformspanel.RowContainer == 'NotAssigned')
                    {
                       return jetformspanel.createRow(main);
                   }

                    return jetformspanel.RowContainer  ;
                }

                jetformspanel.createHalfPannel = function(main,JSONArray)
                {
                    let halfCont = document.createElement("div");
                    halfCont.classList.add("col-sm-6");
                    halfCont.classList.add("col-lg-6");
                    main.appendChild(halfCont);
                    let title = document.createElement("div");
                    title.classList.add("dash-unit");
                    halfCont.appendChild(title);
                    let titleText = document.createElement("dtitle");
                    titleText.innerHTML = JSONArray.labelname;
                    title.appendChild(titleText);
                    let rule = document.createElement("hr");
                    title.appendChild(rule);
                    let padded = document.createElement('div');
                    padded.classList.add('padded');
                    padded.setAttribute('id',JSONArray.id);
                    title.appendChild(padded);
                }

                jetformspanel.createFullPannel = function(main,JSONArray)
                {


                    let halfCont2 = document.createElement("div");
                    halfCont2.classList.add("col-sm-12");
                    halfCont2.classList.add("col-lg-12");
                    main.appendChild(halfCont2);
                    let title2 = document.createElement("div");
                    title2.classList.add("dash-unit");
                    halfCont2.appendChild(title2);
                    let titleText2 = document.createElement("dtitle");
                    titleText2.innerHTML = JSONArray.labelname;
                    title2.appendChild(titleText2);
                    let rule2 = document.createElement("hr");
                    title2.appendChild(rule2);
                    let padded = document.createElement('div');
                    padded.classList.add('padded');
                    padded.setAttribute('id',JSONArray.id);
                    title2.appendChild(padded);
                }

                jetformspanel.createQuarterPannel = function(main,JSONArray)
                {
                    let halfCont2 = document.createElement("div");
                    halfCont2.classList.add("col-sm-3");
                    halfCont2.classList.add("col-lg-3");
                    main.appendChild(halfCont2);
                    let title2 = document.createElement("div");
                    title2.classList.add("dash-unit");
                    halfCont2.appendChild(title2);
                    let titleText2 = document.createElement("dtitle");
                    titleText2.innerHTML = JSONArray.labelname;
                    title2.appendChild(titleText2);
                    let rule2 = document.createElement("hr");
                    title2.appendChild(rule2);
                    let padded = document.createElement('div');
                    padded.classList.add('padded');
                    padded.setAttribute('id',JSONArray.id);
                    title2.appendChild(padded);
                }


                jetformspanel.DoLayout = function (ObjectToApend,JSONSetup)
                {

                    var main = jetformspanel.mainblock(ObjectToApend,JSONSetup.mainLabelName);


                    for (let i =0 ; i<JSONSetup.subPanels.length ; i++)
                    {

                        if(JSONSetup.subPanels[i].size == 'full')
                        {
                            var full = jetformspanel.createFullPannel(jetformspanel.createRow(main),JSONSetup.subPanels[i]);
                        }



                        if(JSONSetup.subPanels[i].size == 'half'&& i == 0 )
                        {

var halpanel = jetformspanel.createHalfPannel(jetformspanel.createRow(main),JSONSetup.subPanels[i]);
// var myrow = jetformspanel.createRow(main);
//var halpanel = jetformspanel.createHalfPannel(jetformspanel.addRow(myrow),JSONSetup.subPanels[i]);

}else{
    if(JSONSetup.subPanels[i].size == 'half')
    {
        var halpanel = jetformspanel.createHalfPannel(jetformspanel.addRow(main),JSONSetup.subPanels[i]);


    }
}
                        if(JSONSetup.subPanels[i].size == 'quarter')
                        {
                            var quartpanel2 = jetformspanel.createQuarterPannel(jetformspanel.addRow(main),JSONSetup.subPanels[i]);
                        }

                    }

                }



                return jetformspanel;

            }();


            return jetformspanel;


        }());
    }));
}(window.define));
